{    
    nome: payload[0].name,
    sobrenome: payload[0].lastname,
    telefones: payload.phone_number map (
        {
            ddd: $[0 to 1],
            numero: $[2 to (sizeOf($) -1)]            
        }
    ),
    endereco: {
        cep: payload[0].zip_code,
        cidade: payload[0].city,
        estado: payload[0].uf,        
        rua: payload[0].street      
    }
}
